import React from 'react';
import BusinessCalculations from '../components/businessCalculations';

const CalculationsPage = ({businessInfo, businessInfoTypeOther, loanTypes}) => {

    const average= (type, info)=>{
        let number =0;
        let total = 0;
        info.forEach(business => {
            if(business.type == type){
                total += business.amount;
                number += 1;
            }
        });
        let averageLoan = "No business of this type"
        if(number!==0){
            averageLoan = total/number;
        }
        return averageLoan;
    }

    let loanTypeTotal = (type)=>{
        let occurs = 0;
        businessInfo.forEach(business => {
            if(business.type === type){
                occurs+=1;
            }
        });
        return occurs;
        }

    const businessCalcUI = loanTypes.map(type=> {
        let typeAverage = average(type);
        let typeTotal = loanTypeTotal(type);
        return(<BusinessCalculations type={type}  average={typeAverage} total = {typeTotal}/>)
})


    return (
        [businessCalcUI]
    )
}; 

export default CalculationsPage;
