import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`

button, ul, li, input, div{
    padding: 0;
    margin: 0;
    box-sizing: border-box;
}


button{
    background: red;
    color: white;
    border: 1px solid black;
    padding: 1rem;
    font-weight: bold;
    :hover{
        cursor: pointer;
        background: pink;
        color: black;
    };
}
`

export default GlobalStyle;
