import React from 'react';

const InputAndError = ({label, id, onBlur, value, onChange, inputClicked, inputValidated, errorMessage}) => {
    return(
    <>
    <label htmlFor={id}>{label}</label>
    <input id={id} onBlur={onBlur} required value={value} name="name" onChange={onChange}/>
    {inputClicked && !inputValidated ? <p>{errorMessage}</p>:null} 
    </>
    )
}; 

export default InputAndError;
