import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { loanTypeContext, companyLoanContext } from '../context';
import Company from '../components/company';
import InputAndError from '../components/inputAndError';
import SelectAndError from '../components/selectAndError';
import LoanRequest from '../components/loanInfo';
import styled from 'styled-components';
import { Redirect, useHistory } from 'react-router-dom';
import { average, tags, loanAmounts, bandCounter2, loanBandCounter, frequencyAndTotal } from '../calculationFunctions';
import Chart from "react-apexcharts";

const StyledUl = styled.ul`
display: flex;
flex-direction: column;
width: 400px;
margin-top: 5rem;
`

const StyledChart = styled(Chart)`
border: black solid 1px;
margin: 4rem;
`

const LoansAndData = (props) => {
    const [companies, setCompanies] = useState([]); 
    const startFormData = {name: "", amount: "", type: "DEFAULT", otherType: ""};
    const {loanTypes} = useContext(loanTypeContext);
    const [loanData, setLoanData] = useState(startFormData);
    const {companyLoanDetails, setCompanyLoanDetails} = useContext(companyLoanContext);
    const [inputValidated, setInputValidated] = useState({name: false, amount: false, type: false, otherType: true})
    const [inputClicked, setInputClicked] = useState({name: false, amount: false, type: false, otherType: false});

    const [chartConfig, setChartConfig] = useState(null);



    const history = useHistory();



    useEffect(()=> {
        getCompanies();
    }, [])

    const getCompanies = async ()=>{
        let response = await axios.get('http://localhost:5000/loans');
        setCompanies(response.data.companies);
        let response2 = frequencyAndTotal(loanTypes, response.data.companies);
        let dataTypes = Object.keys(response2);
        let averages = Object.values(response2).map(data=> data.frequency !== 0 ? data.totalAmount/data.frequency : 0);

        setChartConfig({
            options: {
                plotOptions: {
                    bar: {
                        distributed: true
                    }
                  },
                  legend: {
                    show: false
                  }, 
              chart: {
                id: "basic-bar"
              },
              xaxis: {
                categories: dataTypes,
                labels: {trim:false}
              }
            },
            series: [
              {
                name: "Average",
                data: averages
              }
            ]
          });
    }



    const addLoan = async (e)=>{
        e.preventDefault();
        let data = {name: loanData.name, amount: loanData.amount, type: loanData.type, otherType: loanData.otherType};
        await axios.post('http://localhost:5000/loans', data);
        setLoanData(startFormData);
        getCompanies();        
    }

    const dropDownChangeHandler = (e)=>{
        let {value} = e.target;
        if(value==="Other"){
            setLoanData({...loanData, type: value})
            setInputValidated({...inputValidated, otherType: false})
        }
        else{
            setLoanData({...loanData, type: value, otherType: ""});
            setInputValidated({...inputValidated, otherType: true})
        }
    }

    const onChangeHandler = (e)=>{
        let {name, value} = e.target;
        setLoanData({...loanData, [name]: value});
    }

    const showLoanDetails = (companyId)=>{
        let clickedCompany = companies.find(company=> company._id===companyId);
        setCompanyLoanDetails(clickedCompany);
        history.push('/details');

    }
    const deleteCompany = async (companyId)=>{
        await axios.delete('http://localhost:5000/loans/' + companyId);
        let updatedCompanies = [...companies].filter(company=>company._id!==companyId);
        setCompanies(updatedCompanies);
    }

    const inputUsed = (name)=>{
        if(inputClicked[name]===false){
            setInputClicked({...inputClicked, [name]: true})
        }
    }

    const onBlurHandler = (e)=>{
        let {name, value} = e.target;
        inputUsed(name);
        isFieldValid(name, value);
    }

    const isFieldValid = (name, value)=>{
        let validator = true;
        if(value===""){
            validator = "Please insert a value";
            setInputValidated({...inputValidated, [name]: validator});
            return
        }

        switch(name) {

        case "type":
            if(value==="DEFAULT"){
                validator = "Please choose a type"
            }
            break;

        case "amount":
            console.log(name);
            if(value < 15000){
                validator = "Please insert an amount greater than Â£15000"
            }
            else if(value%1000!==0){
                validator = "Please insert an amount that is a multiple of 1000"  
            }   
            break;     
    }
    setInputValidated({...inputValidated, [name]: validator});
}

let formValid = ()=>{
    for (let key in inputValidated){
        if(inputValidated[key]!==true){
            return false;
        }
    }
    return true;
}


  let companiesUI;
    if(companies!==[]){
        companiesUI = companies.map(company=>{
            return <Company showLoanDetails={()=>showLoanDetails(company._id)} key={company._id} name = {company.name} deleteCompany={()=>deleteCompany(company._id)}>
                {company.loans.map((loan)=> <LoanRequest amount={loan.amount} type={loan.type}/>)}
            </Company>
        })
    }

    const optionElements = [<option key="-1" value="DEFAULT" disabled>Loan Type</option>, loanTypes.map((loanType, index)=><option key={index} value={loanType}>{loanType}</option>)];


    return(
        <>
        <form onSubmit={(e)=>addLoan(e)} >
            <InputAndError id="nameInput" label="Name" name="name" value={loanData.name} onChange={(e)=>onChangeHandler(e)} onBlur={(e)=>onBlurHandler(e)} errorMessage={inputValidated.name} inputClicked={inputClicked.name} inputValidated={inputValidated.name}/>
            <InputAndError id="amountInput" label="Amount" name="amount" value={loanData.amount} onChange={(e)=>onChangeHandler(e)} onBlur={(e)=>onBlurHandler(e)} errorMessage={inputValidated.amount} inputClicked={inputClicked.amount} inputValidated={inputValidated.amount}/>
            <SelectAndError id="loanType" label="Type" onBlur={(e)=>onBlurHandler(e)} required name="type" value={loanData.type} onChange={(e)=>dropDownChangeHandler(e)} errorMessage={inputValidated.type} inputClicked={inputClicked.type} inputValidated={inputValidated.type}>
                {optionElements}
            </SelectAndError>
            {loanData.type==="Other" ? 
            <InputAndError id="otherType" label="Other Type" name="otherType" value={loanData.otherType} onChange={(e)=>onChangeHandler(e)} onBlur={(e)=>onBlurHandler(e)} errorMessage={inputValidated.otherType} inputClicked={inputClicked.otherType} inputValidated={inputValidated.otherType}/>
            : null}
            <button disabled={!formValid()}>Add Loan Request</button>
        </form>
        <div>
            <h1>Businesses</h1>

            {companies!==[] ?
            <> 
            <p>Click Business to Show Further Details</p>
            <StyledUl>{companiesUI}</StyledUl>
            </>
             : <p>Currently no businesses</p>}
        </div>
        {chartConfig ? <StyledChart
              options={chartConfig.options}
              series={chartConfig.series}
              type="bar"
              width="700"
            />:null
        }
        </>
    )
}; 

export default LoansAndData;
