import React, { useState, useEffect, Component } from 'react';
import Todo from '../components/todo';
import styled from 'styled-components';
const URL = 'ws://localhost:5000';


const StyledUl = styled.ul`
display: flex;
flex-direction: column;
padding: 0;
margin: 0;
width: 100%;
`

const StyledDiv = styled.div`
display:flex;
flex-direction: column;
align-items: center;
margin: 0 auto;
margin-top: 1rem;
border: 1px solid black;
width: 20rem;
background: white;
`

const StyledForm = styled.form`
display: flex;
margin-bottom: 1rem;
margin-top: 1rem;
background: blue;
`

class ListPage extends Component {

    state = {
        input: '',
        listItems: ["hi", "you", "are"]
    }

    ws = new WebSocket(URL);

    componentDidMount() {
        this.ws.onopen = (evt) => {
            // on connecting, do nothing but log it to the console
            console.log('connected');
            let data = JSON.stringify({userId: 3});
            this.ws.send(data);
          }

          this.ws.onmessage = evt => {
            // on receiving a message, add it to the list of messages
            const message = JSON.parse(evt.data)
            this.addMessage(message)
          }

          this.ws.onclose = () => {
            console.log('disconnected')
            // automatically try to reconnect on connection loss
            this.setState({
              ws: new WebSocket(URL),
            })
          }
        }




    saveListItem = (e)=>{
        e.preventDefault();
        if(this.state.input!==""){
            // ws.send(input);
            this.setState({...this.state, listItems: [...this.state.listItems, this.state.input]});
            this.setState({...this.state, input: ""});
        }
    }

    removeListItem = (index)=>{
        const updatedListItems = [...this.state.listItems];
        updatedListItems.splice(index, 1);
        this.setState({...this.state, listItems: updatedListItems});
    }

    render(){

    let todoList = this.state.listItems.map((item, index)=><Todo key={index} onclick={()=>this.removeListItem(index)} todo={item}/>)

    return(
        <StyledDiv>
        <StyledForm onSubmit={(e)=>this.saveListItem(e)}>
            <input value={this.state.input} onChange={(e)=>setInput(e.target.value)}/>
            <button>Add Todo</button>
        </StyledForm>
        {todoList.length!==0 ? <StyledUl>{todoList}</StyledUl>: null}

        </StyledDiv>
    )}
}; 

export default ListPage;
