import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import LoanInfo from '../components/loanInfo';
import { loanTypeContext } from '../context';
import Company from '../components/company';

const LoansAndData = (props) => {
    const [companyLoans, setCompanyLoans] = useState([]); 
    const startFormData = {name: "", amount: "", type: "DEFAULT", otherType: ""};
    const {loanTypes} = useContext(loanTypeContext);
    const [loanData, setLoanData] = useState(startFormData);
    const [otherType, setOtherType] = useState(false);

    useEffect(()=> {
        getCompanyLoans();
    }, [])

    const getCompanyLoans = async ()=>{
        let response = await axios.get('http://localhost:5000/loans');
        setCompanyLoans(response.data.companies);
    }

    const addLoan = async (e)=>{
        e.preventDefault();
        let data = {name: loanData.name, amount: loanData.amount, type: loanData.type, otherType: loanData.otherType};
        await axios.post('http://localhost:5000/loans', data);
        setLoanData(startFormData);
        getCompanyLoans();

    }

    const dropDownChangeHandler = (e)=>{
        let {value} = e.target;
        if(value==="Other"){
            setLoanData({...loanData, type: value})
        }
        else{
            setLoanData({...loanData, type: value, otherType: ""})
        }
    }

    const onChangeHandler = (e)=>{
        let {name, value} = e.target;
        setLoanData({...loanData, [name]: value});
    }

    // const deleteLoan = (loanId)=>{
    //     await axios.delete('http://localhost:5000/loans/' + loanId)
    // }

  let companyLoansUI;
    if(companyLoans!==[]){
        companyLoansUI = companyLoans.map(company=>{
            return <Company key={company._id} name = {company.name}>{company.loans.map((loan, index)=> <LoanInfo key={loan._id} loanNumber={index+1} name={company.name} amount={loan.amount} type={loan.type} otherType={loan.otherType}/>)}</Company>
        })
    }


    const optionElements = loanTypes.map((loanType, index)=><option key={index} value={loanType}>{loanType}</option>)

    return(
        <>
        <form onSubmit={(e)=>addLoan(e)} >
            <input required value={loanData.name} name="name" onChange={(e)=>onChangeHandler(e)} placeholder="Business Name"/>
            <input type="number" required value={loanData.amount} name="amount" onChange={(e)=>onChangeHandler(e)} placeholder="Loan Amount" />
            <select required name="loan types" name="type"  value={loanData.type} onChange={(e)=>dropDownChangeHandler(e)}>
            <option value="DEFAULT" disabled>Loan Type</option>
                {optionElements}
            </select>
            {loanData.type==="Other" ? <input required value={loanData.otherType} name="otherType" onChange={(e)=>{onChangeHandler(e)}} placeholder="Loan Type"/>: null}
            <button>Add Loan Request</button>
        </form>
        {companyLoans!==[] ? companyLoansUI: null}
        </>
    )
}; 

export default LoansAndData;
