import React from 'react';
import styled from 'styled-components';

const StyledLi = styled.li`
display: flex;
list-style: none;
background: blue;
color: white;
margin-bottom: 1rem;
height: 2rem;
justify-content: center;
align-items: center;
`

const StyledBtn = styled.button`
position: absolute;
padding-bottom: -10px;
`


const Todo = ({todo, onclick}) => {
    return(
        <div>
        <StyledLi>{todo}</StyledLi>
        <StyledBtn onClick={onclick}>X</StyledBtn>
        </div>
    ) 
}; 

export default Todo;
