import React from 'react';
import BusinessCalculations from '../components/businessCalculations';

const CalculationsPage = ({businessInfo}) => {

    const types = ["Acquire a new vehicle for my business","Acquire a new piece of equipment","Buy a property and to rent it out","Buy another business","Export overseas","Recruit new staff","Other"];

    const average= (type)=>{
        let number =0;
        let total = 0;
        businessInfo.forEach(business => {
            if(business.type === type){
                total += business.amount;
                number += 1;
            }
        });
        let averageLoan = total/number;

        return averageLoan;
    }

    let loanTypeTotal = (type)=>{
        let occurs = 0;
        businessInfo.forEach(business => {
            if(business.type === type){
                occurs+=1;
            }
        });
        return occurs;
        }

    const businessCalcUI = types.map(type=> {
        let typeAverage = average(type);
        let typeTotal = loanTypeTotal(type);
        <BusinessCalculations type={type}  average={typeAverage} total = {typeTotal}/> 
})


    return (
        {businessCalcUI}
    )
}; 

export default CalculationsPage;
