import React, {  useState, useContext, Component } from 'react';
import axios from 'axios';
import { loanTypeContext, companyLoanContext } from '../context';
import Company from '../components/company';
import InputAndError from '../components/inputAndError';
import SelectAndError from '../components/selectAndError';
import LoanRequest from '../components/loanInfo';
import styled from 'styled-components';
import { Redirect, useHistory } from 'react-router-dom';
import { average, tags, loanAmounts, bandCounter2, loanBandCounter, frequencyAndTotal } from '../calculationFunctions';
import Chart from "react-apexcharts";
import { loanTypes, initialChartConfig } from '../config';


const StyledUl = styled.ul`
display: flex;
flex-direction: column;
width: 400px;
margin-top: 5rem;
`

const StyledChart = styled(Chart)`
border: black solid 1px;
margin: 4rem;
width: 700px;
`

class LoansAndData extends Component {
    static contextType = companyLoanContext

    componentDidMount (){
        axios.get('http://localhost:5000/loans')
        .then(response => {
            let response2 = frequencyAndTotal(loanTypes, response.data.companies);
            let dataTypes = Object.keys(response2);
            let frequencyAndTotals = Object.values(response2)
            let averages = frequencyAndTotals.map(data=> data.frequency !== 0 ? data.totalAmount/data.frequency : 0);
            let typeFrequencies = frequencyAndTotals.map(data=>data.frequency);
            this.setState({...this.state, 
            chartConfig: {...this.state.chartConfig, options: {...this.state.chartConfig.options, xaxis: {...this.state.chartConfig.options.xaxis, categories: dataTypes}}, series: [{name:"Averages", data: averages}]},
            companies: response.data.companies,
            calculationsData: {averages: averages, frequencies: typeFrequencies}
        });
    })
}

    constructor(props) {
        super(props);

        this.initialFormData = {name: "", amount: "", type: "DEFAULT", otherType: ""};

        this.state = {
            chartConfig: initialChartConfig,
            companies: [],
            loanTypes: loanTypes,
            loanData: this.initialFormData,
            inputValidated: {name: false, amount: false, type: false, otherType: true},
            inputClicked: {name: false, amount: false, type: false, otherType: false},
            calculationsData: {},

        }
    }




    addLoan = async (e)=>{
        e.preventDefault();
        let data = {name: this.state.loanData.name, amount: this.state.loanData.amount, type: this.state.loanData.type, otherType: this.state.loanData.otherType};
        await axios.post('http://localhost:5000/loans', data);
        this.setState({...this.state, loanData: this.initialFormData});
        this.getCompanies();        
    }

    dropDownChangeHandler = (e)=>{
        let {value} = e.target;
        if(value==="Other"){
            this.setState({...this.state, loanData: this.initialFormData, type: value})
            this.setState({...this.state, inputValidated: {...this.state.inputValidated, otherType: false}});
        }
        else{
            this.setState({...this.state, loanData: {...this.state.loanData, type: value, otherType: ""}});
            this.setState({...this.state, inputValidated: {...this.state.inputValidated, otherType: true}});
        }
    }

    onChangeHandler = (e)=>{
        let {name, value} = e.target;
        this.setState({...this.state, loanData: {...this.state.loanData, [name]: value}});
    }


    deleteCompany = async (companyId)=>{
        await axios.delete('http://localhost:5000/loans/' + companyId);
        let updatedCompanies = [...this.state.companies].filter(company=>company._id!==companyId);
        this.setState({...this.state, companies: updatedCompanies});
    }

    inputUsed = (name)=>{
        if(this.state.inputClicked[name]===false){
            this.setState({...this.state, inputClicked: {...this.state.inputClicked, [name]: true}});
        }
    }

    onBlurHandler = (e)=>{
        let {name, value} = e.target;
        this.inputUsed(name);
        this.isFieldValid(name, value);
    }

    isFieldValid = (name, value)=>{
        let validator = true;
        if(value===""){
            validator = "Please insert a value";
            this.setState({...this.state, inputValidated: {...this.state.inputValidated, [name]: validator}});
            return
        }

        switch(name) {

        case "type":
            if(value==="DEFAULT"){
                validator = "Please choose a type"
            }
            break;

        case "amount":
            console.log(name);
            if(value < 15000){
                validator = "Please insert an amount greater than £15000"
            }
            else if(value%1000!==0){
                validator = "Please insert an amount that is a multiple of 1000"  
            }   
            break;     
    }
    this.setState({...this.state, inputValidated: {...this.state.inputValidated, [name]: validator}});
}

formValid = ()=>{
    for (let key in this.state.inputValidated){
        if(this.state.inputValidated[key]!==true){
            return false;
        }
    }
    return true;
}

changeGraph=(e)=>{
    switch (e.target.name){
        case "average":
            this.setState({...this.state, chartConfig: {...this.state.chartConfig, series: [{name: "Averages", data: this.state.calculationsData.averages}]}});
            break;
        case "frequencyType":
            this.setState({...this.state, chartConfig: {...this.state.chartConfig, series: [{name: "Frequencies", data: this.state.calculationsData.frequencies}]}});
            break;
        default:
            console.log("error");       
    }
}




    render(){

        const { companyLoanDetails, setCompanyLoanDetails } = this.context
        let companiesUI;
    if(this.state.companies!==[]){
        companiesUI = this.state.companies.map(company=>{
            return <Company showLoanDetails={()=>showLoanDetails(company._id)} key={company._id} name = {company.name} deleteCompany={()=>this.deleteCompany(company._id)}>
                {company.loans.map((loan)=> <LoanRequest amount={loan.amount} type={loan.type}/>)}
            </Company>
        })
    }
    const optionElements = [<option key="-1" value="DEFAULT" disabled>Loan Type</option>, loanTypes.map((loanType, index)=><option key={index} value={loanType}>{loanType}</option>)];

    const showLoanDetails = (companyId)=>{
        let clickedCompany = this.state.companies.find(company=> company._id===companyId);
        setCompanyLoanDetails(clickedCompany);
        this.props.history.push('/details');

    }

    return(
        <>
        {JSON.stringify(this.state.inputValidated)}
        <form onSubmit={(e)=>this.addLoan(e)} >
            <InputAndError id="nameInput" label="Name" name="name" value={this.state.loanData.name} onChange={(e)=>this.onChangeHandler(e)} onBlur={(e)=>this.onBlurHandler(e)} errorMessage={this.state.inputValidated.name} inputClicked={this.state.inputClicked.name} inputValidated={this.state.inputValidated.name}/>
            <InputAndError id="amountInput" label="Amount" name="amount" value={this.state.loanData.amount} onChange={(e)=>this.onChangeHandler(e)} onBlur={(e)=>this.onBlurHandler(e)} errorMessage={this.state.inputValidated.amount} inputClicked={this.state.inputClicked.amount} inputValidated={this.state.inputValidated.amount}/>
            <SelectAndError id="loanType" label="Type" onBlur={(e)=>this.onBlurHandler(e)} required name="type" value={this.state.loanData.type} onChange={(e)=>this.dropDownChangeHandler(e)} errorMessage={this.state.inputValidated.type} inputClicked={this.state.inputClicked.type} inputValidated={this.state.inputValidated.type}>
                {optionElements}
            </SelectAndError>
            {this.state.loanData.type==="Other" ? 
            <InputAndError id="otherType" label="Other Type" name="otherType" value={this.state.loanData.otherType} onChange={(e)=>this.onChangeHandler(e)} onBlur={(e)=>this.onBlurHandler(e)} errorMessage={this.state.inputValidated.otherType} inputClicked={this.state.inputClicked.otherType} inputValidated={this.state.inputValidated.otherType}/>
            : null}
            <button disabled={!this.formValid()}>Add Loan Request</button>
        </form>
        <div>
            <h1>Businesses</h1>

            {this.state.companies!==[] ?
            <> 
            <p>Click Business to Show Further Details</p>
            <StyledUl>{companiesUI}</StyledUl>
            </>
             : <p>Currently no businesses</p>}
        </div>
            <div>
                <div>
                    <button name="average" onClick={(e)=>this.changeGraph(e)}>Average of Each Loan Type</button>  
                    <button name="frequencyType" onClick={(e)=>this.changeGraph(e)}>Frequency of Each Loan Type</button>
                    <button name="frequencyBands">Frequency of Loans in Each Band</button>
                </div>
                <div className="app">
          <div className="row">
            <div className="mixed-chart">
              <Chart
                options={this.state.chartConfig.options}
                series={this.state.chartConfig.series}
                type="bar"
                width="500"
              />
            </div>
          </div>
        </div>
            </div>
        </>
    )
}; 
}

export default LoansAndData;
