import React from 'react';
import './App.css';
import GlobalStyle from './globalStyles';
import CollectionDetails from './containers/collectionDetails';
import CalculationsPage from './containers/calculationsPage';


function App() {
  const loanTypes = ["Acquire a new vehicle for my business","Acquire a new piece of equipment","Buy a property and to rent it out","Buy another business","Export overseas","Recruit new staff"];
  const allBusinessInfo = [
    {id: 1, name: "FaceNovel", amount: 30000, type: "Recruit new staff"},
    {id: 2, name: "WeVlog", amount: 30000000, type: "Buy another business"},
    {id: 3, name: "LibroPedia", amount: 75000, type: "Borrow in anticipation of a revenue source"},
    {id: 4, name: "Coin Byte", amount: 250000, type: "Recruit new staff"}
]

let businessInfo = [];
let businessInfoTypeOther = []; 


allBusinessInfo.forEach(info => {
  if(loanTypes.includes(info.type)){
    businessInfo.push(info);
  }
  else{
    businessInfoTypeOther.push(info.type);
  }
});





  return (
    <>
    <GlobalStyle />
    <div className="App">
      <CollectionDetails businessInfo={businessInfo}/>
      <CalculationsPage loanTypes={loanTypes} businessInfoTypeOther={businessInfoTypeOther} businessInfo={businessInfo}/>
    </div>
    </>
  );
}

export default App;
