import React from 'react';

const Calculations = ({businessInfo}) => {




const average= (type)=>{
    let number =0;
    let total = 0; 
    for(let i=0; i<businessInfo.length; i++){
        console.log(businessInfo[i].type);
        if(businessInfo[i].type === type){
            total += businessInfo[i].amount;
            number += 1;
        }
    }
    let averageLoan = total/number;

    return averageLoan;
}

let loanTypeTotal = (type)=>{
    let occurs = 0;
    for(let i=0; i<businessInfo.length; i++){
        if(businessInfo[i].type === type){
            occurs+=1;
        }
    }
    return occurs;
}

let specificLoanBands = (amount, minAmount, maxAmount)=>{
    let counter = 0;
    if(amount<=maxAmount && amount>= minAmount){
        counter+=1;
    }
    return counter;
}

let averageType = average("Recruit New Staff");

return(
  <p>Average: {averageType}</p>  
)
}; 

export default Calculations;
