import React, { useState, useEffect } from 'react';
import Todo from '../components/todo';
import styled from 'styled-components';

const StyledUl = styled.ul`
display: flex;
flex-direction: column;
padding: 0;
margin: 0;
width: 100%;
`

const StyledDiv = styled.div`
display:flex;
flex-direction: column;
align-items: center;
margin: 0 auto;
margin-top: 1rem;
border: 1px solid black;
width: 20rem;
background: white;
`

const StyledForm = styled.form`
display: flex;
margin-bottom: 1rem;
margin-top: 1rem;
background: blue;
`

const ListPage = (props) => {

    const [listItems, setListItems] = useState(["hi", "you", "are"]);
    const [input, setInput] = useState("");
    const [ws, setWs] = useState(null);

    useEffect(()=>{
        const URL = 'ws://localhost:5000';
        let ws = new WebSocket(URL);
        setWs(ws);
        ws.onopen = (evt) => {
            // on connecting, do nothing but log it to the console
            console.log('connected');
            // let data = JSON.stringify({userId: userId});
            // this.ws.send(data);
          }

          ws.onmessage = evt => {
            // on receiving a message, add it to the list of messages
            const listItem = JSON.parse(evt.data);
            console.log(listItem);
            setListItems(prev=> [...prev, listItem]);
          }

          ws.onclose = () => {
            console.log('disconnected');
          }

          return () => ws.close();
    }, []);




    const saveListItem = (e)=>{
        e.preventDefault();
        if(input!==""){
            ws.send(input);
            setListItems([...listItems, input]);
            setInput("");
        }
    }

    const removeListItem = (index)=>{
        const updatedListItems = [...listItems];
        updatedListItems.splice(index, 1);
        setListItems(updatedListItems);
    }

    let todoList = listItems.map((item, index)=><Todo key={index} onclick={()=>removeListItem(index)} todo={item}/>)
    console.log(todoList);
    return(
        <StyledDiv>
        <StyledForm onSubmit={(e)=>saveListItem(e)}>
            <input value={input} onChange={(e)=>setInput(e.target.value)}/>
            <button>Add Todo</button>
        </StyledForm>
        {todoList.length!==0 ? <StyledUl>{todoList}</StyledUl>: null}

        </StyledDiv>
    ) 
}; 

export default ListPage;
