import React, { Component } from 'react';
import styled from 'styled-components';
import InputAndError from './inputAndError';
import { isFormValid } from '../formValidation';
import {StyledButton} from '../containers/LoansAndData';
import { loanTypes } from '../config';
import SelectAndError from './selectAndError';


const StyledForm = styled.form`
display: flex;
flex-direction: column;
align-self: flex-end;
border: 1px solid black;
padding: 1rem;
`



class AddLoanForm extends Component {
    constructor(props) {
        super(props);
        this.initialFormData = {name: "", amount: "", type: "DEFAULT", otherType: ""};
        this.state = {
            loanData: this.initialFormData,
            inputValidated: {name: false, amount: false, type: false, otherType: true},
            inputClicked: {name: false, amount: false, type: false, otherType: false}
        }
    }


    optionElements = [<option key="-1" value="DEFAULT" disabled>Loan Type</option>, loanTypes.map((loanType, index)=><option key={index} value={loanType}>{loanType}</option>)];
    render(){

        return(
            <StyledForm onSubmit={this.props.onSubmit} >
                <InputAndError id="nameInput" label="Name" name="name" value={this.state.loanData.name} onChange={(e)=>this.onChangeHandler(e)} onBlur={(e)=>this.onBlurHandler(e)} errorMessage={this.state.inputValidated.name} inputClicked={this.state.inputClicked.name}/>
                <InputAndError id="amountInput" label="Amount(£)" name="amount" value={this.state.loanData.amount} onChange={(e)=>this.onChangeHandler(e)} onBlur={(e)=>this.onBlurHandler(e)} errorMessage={this.state.inputValidated.amount} inputClicked={this.state.inputClicked.amount}/>
                <SelectAndError id="loanType" label="Type" onBlur={(e)=>this.onBlurHandler(e)} required name="type" value={this.state.loanData.type} onChange={(e)=>this.dropDownChangeHandler(e)} errorMessage={this.state.inputValidated.type} inputClicked={this.state.inputClicked.type} inputValidated={this.state.inputValidated.type}>
                    {this.optionElements}
                </SelectAndError>
                {this.state.loanData.type==="Other" ? 
                <InputAndError id="otherType" label="Other Type" name="otherType" value={this.state.loanData.otherType} onChange={(e)=>this.onChangeHandler(e)} onBlur={(e)=>this.onBlurHandler(e)} errorMessage={this.state.inputValidated.otherType} inputClicked={this.state.inputClicked.otherType} inputValidated={this.state.inputValidated.otherType}/>
                : null}
                <StyledButton background="green" disabled={!isFormValid(this.state.inputValidated)}>Add Loan Request</StyledButton>
        </StyledForm>
        )
    } 
}; 

export default AddLoanForm;
