import React, { useContext, useState } from 'react';
import { loanTypeContext } from '../context';
import axios from 'axios';

const AddLoan = ({addLoan}) => {
    const startFormData = {name: "", amount: "", type: "DEFAULT", otherType: ""};
    const {loanTypes} = useContext(loanTypeContext);
    const [loanData, setLoanData] = useState(startFormData);
    const [otherType, setOtherType] = useState(false);
    const optionElements = loanTypes.map((loanType, index)=><option key={index} value={loanType}>{loanType}</option>)

    const dropDownChangeHandler = (e)=>{
        let {value} = e.target;
        if(value==="Other"){
            setLoanData({...loanData, type: value})
        }
        else{
            setLoanData({...loanData, type: value, otherType: ""})
        }
    }

    const onChangeHandler = (e)=>{
        let {name, value} = e.target;
        setLoanData({...loanData, [name]: value});
    }




    return(
        <form onSubmit={addLoan} >
            <input required value={loanData.name} name="name" onChange={(e)=>onChangeHandler(e)} placeholder="Business Name"/>
            <input type="number" required value={loanData.amount} name="amount" onChange={(e)=>onChangeHandler(e)} placeholder="Loan Amount" />
            <select required name="loan types" name="type"  value={loanData.type} onChange={(e)=>dropDownChangeHandler(e)}>
            <option value="DEFAULT" disabled>Loan Type</option>
                {optionElements}
            </select>
            {loanData.type==="Other" ? <input required value={loanData.otherType} name="otherType" onChange={(e)=>{onChangeHandler(e)}} placeholder="Loan Type"/>: null}
            <button>Add Loan Request</button>
        </form>
    ) 
}; 

export default AddLoan;
