    console.log(type);
    let number =0;
    let total = 0;
    let bandCounter = {15000: 0, 25000: 0, 50000: 0, 100000: 0, 250000: 0, 500000: 0, 750000: 0, 1000000: 0, 2000000: 0};
    info.forEach(business => {
        business.loans.forEach(loan=>{
            if(loan.type === type){
                total += loan.amount;
                number += 1;
            }
            let keys = Object.keys(bandCounter);
            if(loan.amount>=15000 && loan.amount<25000) bands[15000] += 1;
            else if (loan.amount>=25000 && loan.amount<50000) bands[25000] += 1;
            else if (loan.amount>=100000 && loan.amount<250000) bands[100000] += 1;
            else if (loan.amount>=250000 && loan.amount<500000) bands[250000] += 1;
            else if (loan.amount>=500000 && loan.amount<750000) bands[500000] += 1;
            else if (loan.amount>=750000 && loan.amount<1000000) bands[750000] += 1;
            else if (loan.amount>=1000000 && loan.amount<2000000) bands[1000000] += 1;
            else if (loan.amount>=2000000) bands[2000000] += 1;
        })
    });
    let averageLoan = total/number;

    return [averageLoan, total, bands, info];
}

