import React, { useContext, useState } from 'react';
import { loanTypeContext } from '../context';

const AddLoan = () => {
    const {loanTypes} = useContext(loanTypeContext);
    const [name, setName] = useState("");
    const [amount, setAmount] = useState("");
    const [loanType, setLoanType] = useState("DEFAULT");
    const optionElements = loanTypes.map((loanType, index)=><option key={index} value={loanType}>{loanType}</option>)


    return(
        <form >
            <input value={name} onChange={(e)=>setName(e.target.value)} placeholder="Business Name"/>
            <input value={amount} onChange={(e)=>setAmount(e.target.value)} placeholder="Loan Amount" />
            <select name="loan types" value={loanType} onChange={(e)=>{setLoanType(e.target.value)}}>
            <option value="DEFAULT" disabled>Loan Type</option>
                {optionElements}
            </select>
            <button>Add Loan Request</button>
        </form>
    ) 
}; 

export default AddLoan;
