import React from 'react';

const Calculations = ({businessInfo}) => {

const types = ["Acquire a new vehicle for my business","Acquire a new piece of equipment","Buy a property and to rent it out","Buy another business","Export overseas","Recruit new staff","Other"];


const average= (type)=>{
    let number =0;
    let total = 0;
    businessInfo.forEach(business => {
        if(business.type === type){
            total += business.amount;
            number += 1;
        }
    });
    let averageLoan = total/number;

    return averageLoan;
}

let loanTypeTotal = (type)=>{
    let occurs = 0;
    businessInfo.forEach(business => {
        if(business.type === type){
            occurs+=1;
        }
    });
    return occurs;
    }


let specificLoanBands = (amount, minAmount, maxAmount)=>{
    let counter = 0;
    if(amount<=maxAmount && amount>= minAmount){
        counter+=1;
    }
    return counter;
}

let averageType = average("Recruit New Staff");

return(
  <p>Average: {averageType}</p>  
)
}; 

export default Calculations;
