import React, { useState } from 'react';
import LoanInfo from '../components/loanInfo';
import BusinessCalculations from '../components/businessCalculations';

const CollectionDetails = ({businessInfo}) => {

    const [individualInfo, setIndividualInfo] = useState(null);

const setInfo = (name, amount, type)=>{
    setIndividualInfo({name: name, amount: amount, type: type})
}

let businessUI = businessInfo.map((info)=>{
    return(<BusinessInfo key={info.id} businessClicked={(name, amount, type)=>setInfo(name, amount, type)} name={info.name} amount={info.amount} type={info.type} />)
})

    return(
        <>
        {businessUI}
        {JSON.stringify(individualInfo)}
        </>

    )
}; 

export default CollectionDetails;
