import React, { useState } from 'react';
import './App.css';
import GlobalStyle from './globalStyles';
import { loanTypeContext, companyLoanContext } from './context';
import LoansAndData from './containers/loansAndData';
import OtherPage from './containers/OtherPage';



function App() {
  const [loanTypes] = useState(["Acquire a new vehicle for my business","Acquire a new piece of equipment","Buy a property and to rent it out","Buy another business","Export overseas","Recruit new staff", "Other"]);
  const [companyLoanDetails, setCompanyLoanDetails] = useState([]);
  const allBusinessInfo = [
    {id: 1, name: "FaceNovel", amount: 30000, type: "Recruit new staff"},
    {id: 2, name: "WeVlog", amount: 30000000, type: "Buy another business"},
    {id: 3, name: "LibroPedia", amount: 75000, type: "Borrow in anticipation of a revenue source"},
    {id: 4, name: "Coin Byte", amount: 250000, type: "Recruit new staff"}
]

let businessInfo = [];
let businessInfoTypeOther = []; 


allBusinessInfo.forEach(info => {
  if(loanTypes.includes(info.type)){
    businessInfo.push(info);
  }
  else{
    businessInfoTypeOther.push(info.type);
  }
});

console.log(companyLoanDetails);



  return (
    <>
    <GlobalStyle />

    <div className="App">      
    <companyLoanContext.Provider value={{companyLoanDetails, setCompanyLoanDetails}}>
      <loanTypeContext.Provider value={{loanTypes}}>
      <LoansAndData/>
      {companyLoanDetails.length!==0 ? <OtherPage/>: null}
      {/* <CollectionDetails businessInfo={businessInfo}/> */}
      {/* <CalculationsPage businessInfoTypeOther={businessInfoTypeOther} businessInfo={businessInfo}/> */}
      </loanTypeContext.Provider>
      </companyLoanContext.Provider>
    </div>
    </>
  );
}

export default App;
