import React, { useEffect, useState } from 'react';
import AddLoan from '../components/addLoan';
import axios from 'axios';
import LoanInfo from '../components/loanInfo';

const LoansAndData = (props) => {
    const [companyLoans, setCompanyLoans] = useState([]); 
    useEffect(()=> async ()=>{
           let response = await axios.get('http://localhost:5000/loans');
            setCompanyLoans(response.data.companies);å
    }, [])

    const companyLoansUI = companyLoans.map((companyLoan)=>{
        return(<LoanInfo name={companyLoan.name} amount={companyLoan.amount} type={companyLoan.type} otherType={companyLoan.otherType} />)
    })



    return(
        <>
        <AddLoan/>
        {companyLoansUI}
        </>
    )
}; 

export default LoansAndData;
