import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { loanTypeContext, companyLoanContext } from '../context';
import Company from '../components/company';

const LoansAndData = (props) => {
    const [companies, setCompanies] = useState([]); 
    const startFormData = {name: "", amount: "", type: "DEFAULT", otherType: ""};
    const {loanTypes} = useContext(loanTypeContext);
    const [loanData, setLoanData] = useState(startFormData);
    const [otherType, setOtherType] = useState(false);
    const {companyLoanDetails, setCompanyLoanDetails} = useContext(companyLoanContext);
    const [inputValidated, setInputValidated] = useState({name: false, amount: false, type: false, otherType: true})

    useEffect(()=> {
        getCompanies();
    }, [])

    const getCompanies = async ()=>{
        let response = await axios.get('http://localhost:5000/loans');
        setCompanies(response.data.companies);
    }

    const addLoan = async (e)=>{
        e.preventDefault();
        let data = {name: loanData.name, amount: loanData.amount, type: loanData.type, otherType: loanData.otherType};
        await axios.post('http://localhost:5000/loans', data);
        setLoanData(startFormData);
        getCompanies();        
    }

    const dropDownChangeHandler = (e)=>{
        let {value} = e.target;
        if(value==="Other"){
            setLoanData({...loanData, type: value})
            setInputValidated({...inputValidated, otherType: false})
        }
        else{
            setLoanData({...loanData, type: value, otherType: ""});
            setInputValidated({...inputValidated, otherType: true})
        }
    }

    const onChangeHandler = (e)=>{
        let {name, value} = e.target;
        setLoanData({...loanData, [name]: value});
    }

    const showLoanDetails = (companyId)=>{
        let clickedCompany = companies.find(company=> company._id===companyId);
        setCompanyLoanDetails(clickedCompany);
    }
    const deleteCompany = async (companyId)=>{
        await axios.delete('http://localhost:5000/loans/' + companyId);
        let updatedCompanies = [...companies].filter(company=>company._id!==companyId);
        setCompanies(updatedCompanies);
    }

    const isFieldValid = (e)=>{
        let {name,value} = e.target;
        let validator = true;
        if(value===""){
            validator = "Please insert a value";
            setInputValidated({...inputValidated, [name]: validator});
            return
        }

        switch(name) {

        case "type":
            if(value==="DEFAULT"){
                validator = "Please choose a type"
            }
            break;

        case "amount":
            console.log(name);
            if(value < 15000){
                validator = "Please insert an amount greater than £15000"
            }
            else if(value%1000!==0){
                validator = "Please insert an amount that is a multiple of 1000"  
            }   
            break;     
    }
    setInputValidated({...inputValidated, [name]: validator});
}

let formValid = ()=>{
    for (let key in inputValidated){
        if(inputValidated[key]!==true){
            return false;
        }
    }
    return true;
}


  let companiesUI;
    if(companies!==[]){
        companiesUI = companies.map(company=>{
            return <Company showLoanDetails={()=>showLoanDetails(company._id)} key={company._id} name = {company.name} deleteCompany={()=>deleteCompany(company._id)}/>
        })
    }

    const optionElements = loanTypes.map((loanType, index)=><option key={index} value={loanType}>{loanType}</option>)



    return(
        <>
        <form onSubmit={(e)=>addLoan(e)} >
            <label htmlFor="nameInput">Name</label>
            <input id="nameInput" onBlur={(e)=>isFieldValid(e)} required value={loanData.name} name="name" onChange={(e)=>onChangeHandler(e)} placeholder="Business Name"/>
            <label htmlFor="amountInput">Amount</label>
            <input id="amountInput" onBlur={(e)=>isFieldValid(e)} type="number" required value={loanData.amount} name="amount" onChange={(e)=>onChangeHandler(e)} placeholder="Loan Amount" />
            <label htmlFor="loanType">Type</label>
            <select id="loanType" onBlur={(e)=>isFieldValid(e)} required name="type" value={loanData.type} onChange={(e)=>dropDownChangeHandler(e)}>
            <option value="DEFAULT" disabled>Loan Type</option>
                {optionElements}
            </select>
            {loanData.type==="Other" ? 
            <>
            <label htmlFor="typeDetails">Type Details</label>
            <input id="typeDetails" onBlur={(e)=>isFieldValid(e)} required value={loanData.otherType} name="otherType" onChange={(e)=>{onChangeHandler(e)}} placeholder="Type Details"/>
            </>
            : null}
            <button disabled={!formValid}>Add Loan Request</button>
        </form>
        {companies!==[] ? companiesUI: null}
        {JSON.stringify(inputValidated)}
        </>
    )
}; 

export default LoansAndData;
