import React, { Component } from 'react';
import axios from 'axios';
import { loanTypeContext, companyLoanContext } from '../context';
import Company from '../components/company';
import InputAndError from '../components/inputAndError';
import SelectAndError from '../components/selectAndError';
import LoanRequest from '../components/loanInfo';
import styled from 'styled-components';
import { frequencyAndTotal, loanBandCounter } from '../calculationFunctions';
import Chart from "react-apexcharts";
import { loanTypes, initialChartConfig, bandCounter, bandGroups } from '../config';
import { isFormValid, isFieldValid } from '../formValidation';

const StyledUl = styled.ul`
display: flex;
flex-direction: column;
width: 400px;
margin-top: 5rem;
`

const StyledChart = styled(Chart)`
border: black solid 1px;
margin: 4rem;
width: 700px;
`

const StyledButton = styled.button`
background: ${props=>{
    if(props.active==="isactive"){return "blue";}
}};
`

class LoansAndData extends Component {
    static contextType = companyLoanContext

    componentDidMount (){
        this.getCompanies();
}

    constructor(props) {
        super(props);

        this.initialFormData = {name: "", amount: "", type: "DEFAULT", otherType: ""};

        this.state = {
            chartConfig: initialChartConfig,
            companies: [],
            loanTypes: loanTypes,
            loanData: this.initialFormData,
            inputValidated: {name: false, amount: false, type: false, otherType: true},
            inputClicked: {name: false, amount: false, type: false, otherType: false},
            calculationsData: {},
            activeGraph: "Average"
        }
    }


    getCompanies = ()=>{
        axios.get('http://localhost:5000/loans')
        .then(response => {
            let response2 = frequencyAndTotal(loanTypes, response.data.companies);
            let dataTypes = Object.keys(response2);
            let frequencyAndTotals = Object.values(response2)
            let averages = frequencyAndTotals.map(data=> data.frequency !== 0 ? Math.round(data.totalAmount/data.frequency).toString() : 0);
            let typeFrequencies = frequencyAndTotals.map(data=>data.frequency);
            let bandCounterObject = loanBandCounter(response.data.companies, bandCounter);
            let bandFrequencies = Object.values(bandCounterObject);
            console.log(bandFrequencies);
            this.setState({...this.state, 
            companies: response.data.companies,
            calculationsData: {averages: averages, frequencies: typeFrequencies, bandFrequency: bandFrequencies}
    })
    console.log(bandCounterObject);
    let graphName = this.state.chartConfig.series[0].name;
    if(graphName==="Averages"||graphName==="") this.setState({...this.state, chartConfig: {...this.state.chartConfig, options: {...this.state.chartConfig.options, xaxis: {...this.state.chartConfig.options.xaxis, categories: dataTypes}}, series: [{name:"Averages", data: averages}]}});
    else if (graphName==="Frequency (Type)")  this.setState({...this.state, chartConfig: {...this.state.chartConfig, options: {...this.state.chartConfig.options, xaxis: {...this.state.chartConfig.options.xaxis, categories: dataTypes}}, series: [{name:"Frequencies (Type)", data: typeFrequencies}]}});
    else if (graphName==="Frequency (Bands)") this.setState({...this.state, chartConfig: {...this.state.chartConfig, options: {...this.state.chartConfig.options, xaxis: {...this.state.chartConfig.options.xaxis, categories: bandGroups}}, series: [{name:"Frequencies (Band)", data: bandFrequencies}]}});
})
    }


    addLoan = async (e)=>{
        e.preventDefault();
        let data = {name: this.state.loanData.name, amount: this.state.loanData.amount, type: this.state.loanData.type, otherType: this.state.loanData.otherType};
        await axios.post('http://localhost:5000/loans', data);
        this.setState({...this.state, loanData: this.initialFormData});
        this.getCompanies();        
    }

    dropDownChangeHandler = (e)=>{
        let {value} = e.target;
        if(value==="Other"){
            this.setState({...this.state, 
                loanData: {...this.initialFormData, type: value},
                inputValidated: {...this.state.inputValidated, otherType: false}});
        }
        else{
            this.setState({...this.state, 
                loanData: {...this.state.loanData, type: value, otherType: ""},
                inputValidated: {...this.state.inputValidated, otherType: true}});
        }
    }

    onChangeHandler = (e)=>{
        let {name, value} = e.target;
        this.setState({...this.state, loanData: {...this.state.loanData, [name]: value}});
    }


    deleteCompany = async (e, companyId)=>{
        e.stopPropagation();
        await axios.delete('http://localhost:5000/loans/' + companyId);
        // let updatedCompanies = [...this.state.companies].filter(company=>company._id!==companyId);
        // this.setState({...this.state, companies: updatedCompanies});
        this.getCompanies();
    }



    onBlurHandler = (e)=>{
        let {name, value} = e.target;
        let validator = isFieldValid(name, value);

        this.setState({...this.state, 
            inputClicked: {...this.state.inputClicked, [name]: true},
            inputValidated: {...this.state.inputValidated, [name]: validator}
        })
    }



changeGraph=(e)=>{
    let name;
    let data;
    let categories;
    let activeGraph;
    switch (e.target.name){
        case "average":
            name = "Averages";
            data = this.state.calculationsData.averages;
            categories = loanTypes;
            activeGraph = "Average"
            break;
        case "frequencyType":
            name = "Frequency (Type)";
            data = this.state.calculationsData.frequencies;
            categories = loanTypes;
            activeGraph = "Frequency (Type)"
            break;

        case "frequencyBands":
            name = "Frequency (Bands)";
            data = this.state.calculationsData.bandFrequency;
            categories = bandGroups;
            activeGraph = "Frequency (Bands)"
            break;
        default:
            console.log("error");     
    }

    this.setState({...this.state, 
        chartConfig: {...this.state.chartConfig, options:{...this.state.chartConfig.options, xaxis: {...this.state.chartConfig.options.xaxis, categories: categories}}, series: [{name: name, data: data}]},
        activeGraph: activeGraph
});
}




    render(){

        const { companyLoanDetails, setCompanyLoanDetails } = this.context
        let companiesUI;
    if(this.state.companies!==[]){
        companiesUI = this.state.companies.map(company=>{
            return <Company showLoanDetails={(e)=>showLoanDetails(e, company._id)} key={company._id} name = {company.name} deleteCompany={(e)=>this.deleteCompany(e, company._id)}>
                {company.loans.map((loan)=> <LoanRequest amount={loan.amount} type={loan.type}/>)}
            </Company>
        })
    }
    const optionElements = [<option key="-1" value="DEFAULT" disabled>Loan Type</option>, loanTypes.map((loanType, index)=><option key={index} value={loanType}>{loanType}</option>)];

    const showLoanDetails = (e, companyId)=>{
        let clickedCompany = this.state.companies.find(company=> company._id===companyId);
        setCompanyLoanDetails(clickedCompany);
        this.props.history.push('/details');

    }

    return(
        <>
        {JSON.stringify(this.state.chartConfig)}
        <form onSubmit={(e)=>this.addLoan(e)} >
            <InputAndError id="nameInput" label="Name" name="name" value={this.state.loanData.name} onChange={(e)=>this.onChangeHandler(e)} onBlur={(e)=>this.onBlurHandler(e)} errorMessage={this.state.inputValidated.name} inputClicked={this.state.inputClicked.name}/>
            <InputAndError id="amountInput" label="Amount" name="amount" value={this.state.loanData.amount} onChange={(e)=>this.onChangeHandler(e)} onBlur={(e)=>this.onBlurHandler(e)} errorMessage={this.state.inputValidated.amount} inputClicked={this.state.inputClicked.amount}/>
            <SelectAndError id="loanType" label="Type" onBlur={(e)=>this.onBlurHandler(e)} required name="type" value={this.state.loanData.type} onChange={(e)=>this.dropDownChangeHandler(e)} errorMessage={this.state.inputValidated.type} inputClicked={this.state.inputClicked.type} inputValidated={this.state.inputValidated.type}>
                {optionElements}
            </SelectAndError>
            {this.state.loanData.type==="Other" ? 
            <InputAndError id="otherType" label="Other Type" name="otherType" value={this.state.loanData.otherType} onChange={(e)=>this.onChangeHandler(e)} onBlur={(e)=>this.onBlurHandler(e)} errorMessage={this.state.inputValidated.otherType} inputClicked={this.state.inputClicked.otherType} inputValidated={this.state.inputValidated.otherType}/>
            : null}
            <button disabled={!isFormValid(this.state.inputValidated)}>Add Loan Request</button>
        </form>
        <div>
            <h1>Businesses</h1>

            {this.state.companies!==[] ?
            <> 
            <p>Click Business to Show Further Details</p>
            <StyledUl>{companiesUI}</StyledUl>
            </>
             : <p>Currently no businesses</p>}
        </div>
            <div>
                <div>
                    <StyledButton active={this.state.activeGraph === "Average" ? "isactive" : " "} name="average" onClick={(e)=>this.changeGraph(e)}>Average of Each Loan Type</StyledButton>  
                    <StyledButton active={this.state.activeGraph === "Frequency (Type)" ? "isactive" : " "} name="frequencyType" onClick={(e)=>this.changeGraph(e)}>Frequency of Each Loan Type</StyledButton>
                    <StyledButton active={this.state.activeGraph === "Frequency (Bands)" ? "isactive" : " "} name="frequencyBands" onClick={(e)=>this.changeGraph(e)}>Frequency of Loans in Each Band</StyledButton>
                </div>
                <div className="app">
          <div className="row">
            <div className="mixed-chart">
              <Chart
