import React, { useContext, useState } from 'react';
import { loanTypeContext } from '../context';
import axios from 'axios';

const AddLoan = () => {
    const {loanTypes} = useContext(loanTypeContext);
    const [name, setName] = useState("");
    const [amount, setAmount] = useState("");
    const [loanType, setLoanType] = useState("DEFAULT");
    const [otherType, setOtherType] = useState(false);
    const [otherTypeDetails, setOtherTypeDetails] = useState("");
    const optionElements = loanTypes.map((loanType, index)=><option key={index} value={loanType}>{loanType}</option>)

    const addLoan = async (e)=>{
        e.preventDefault();
        let data = {name: name, amount: amount, loanType: loanType};
        let response = await axios.post('http://localhost:5000/loans', data);
    }

    const dropDownChangeHandler = (e)=>{
        setLoanType(e.target.value);
        if(e.target.value==="Other"){
            setOtherType(true);
        }
        else{
            setOtherType(false);
        }
    }

    return(
        <form onSubmit={(e)=>addLoan(e)} >
            <input value={name} onChange={(e)=>setName(e.target.value)} placeholder="Business Name"/>
            <input value={amount} onChange={(e)=>setAmount(e.target.value)} placeholder="Loan Amount" />
            <select name="loan types" value={loanType} onChange={(e)=>dropDownChangeHandler(e)}>
            <option value="DEFAULT" disabled>Loan Type</option>
                {optionElements}
            </select>
            {otherType ? <input placeholder="Loan Type"/>: null}
            <button>Add Loan Request</button>
        </form>
    ) 
}; 

export default AddLoan;
