import React, { useState } from 'react';
import Todo from '../components/todo';
import styled from 'styled-components';

const StyledUl = styled.ul`
display: flex;
flex-direction: column;
padding: 0;
margin: 0 auto;
width: 15rem;
border-left: 1px solid black;
border-right: 1px solid black;
border-bottom: 1px solid black;
padding: 1rem;
margin-top: 5rem;
`

const ListPage = (props) => {

    const [listItems, setListItems] = useState(["hi", "you", "are"]);
    const [input, setInput] = useState("");

    const saveListItem = (e)=>{
        e.preventDefault();
        if(input!==""){
            setListItems([...listItems, input]);
            setInput("");
        }
    }

    const removeListItem = (index)=>{
        const updatedListItems = [...listItems];
        updatedListItems.splice(index, 1);
        setListItems(updatedListItems);
    }

    let todoList = listItems.map((item, index)=><Todo key={index} onclick={()=>removeListItem(index)} todo={item}/>)

    return(
        <>
        <form onSubmit={(e)=>saveListItem(e)}>
            <input value={input} onChange={(e)=>setInput(e.target.value)}/>
            <button>Submit Todo</button>
        </form>

            {todoList ? <StyledUl>{todoList}</StyledUl>: null}

        </>
    ) 
}; 

export default ListPage;
