import React, { useState } from 'react';
import Todo from '../components/todo';


const ListPage = (props) => {

    const [listItems, setListItems] = useState([]);
    const [input, setInput] = useState("");

    const saveListItem = (e)=>{
        e.preventDefault();
        setListItems([...listItems, input]);
    }

    const removeListItem = (index)=>{
        console.log(index);
        console.log(listItems);
        const updatedListItems = listItems.splice(index, 0);
        console.log(updatedListItems);
        setListItems(updatedListItems);
    }

    let todoList = listItems.map((item, index)=><Todo onclick={()=>removeListItem(index)} todo={item}/>)

    return(
        <>
        <form onSubmit={(e)=>saveListItem(e)}>
            <input value={input} onChange={(e)=>setInput(e.target.value)}/>
            <button>Submit Todo</button>
        </form>

        {todoList}
        </>
    ) 
}; 

export default ListPage;
