import React, { useEffect, useState } from 'react';
import AddLoan from '../components/addLoan';
import axios from 'axios';
import LoanInfo from '../components/loanInfo';

const LoansAndData = (props) => {
    const [companyLoans, setCompanyLoans] = useState([]); 
    useEffect(async ()=>{
           let response = await axios.get('http://localhost:5000/loans');
            setCompanyLoans(response.data.companies);
    }, [])


    let companyLoansUI = companyLoans.map(company=>{
        let name = company.name;
        return company.loans.map((loan)=>{
            <LoanInfo name={name} amount={loan.amount} type={loan.type} otherType={loan.otherType}/> 
        })
    })



    return(
        <>
        <AddLoan/>
        {companyLoansUI}
        </>
    )
}; 

export default LoansAndData;
